  text-shadow: 0 0 10px #03bcf4, 0 0 20px #03bcf4, 0 0 40px #03bcf4,
    0 0 80px #03bcf4, 0 0 160px #03bcf4;
  text-align: center;

  line-height: 0.7em;
  outline: none;
`;

export const Tittle = styled.h2`
